INSERT INTO events (id, period_id, title, description, date, location, location_coordinates_lat, location_coordinates_lng, theme, sources, created_at, updated_at) VALUES
(1, 1, 'Apparition d''Homo sapiens', 'Émergence de l''homme moderne en Afrique, marquant le début de l''histoire humaine.', '-300000-01-01', 'Afrique (premiers sites notables : Jebel Irhoud, Maroc)', 31.86000000, -8.87000000, '["Origines", "Évolution", "Anthropologie"]', '["Leakey, Richard E., and Roger Lewin. Origins. Simon & Schuster, 1977."]', NOW(), NOW()),
(2, 1, 'Peintures rupestres de Lascaux', 'Découverte de peintures pariétales exceptionnelles, témoignage de l''expression artistique et symbolique des hommes préhistoriques.', '-17000-01-01', 'Montignac, Dordogne, France', 45.05000000, 1.17000000, '["Art", "Symbolisme", "Préhistoire"]', '["Bataille, Georges. Lascaux ou la naissance de l''art. Skira, 1955."]', NOW(), NOW()),
(3, 1, 'Construction de Stonehenge', 'Érection d''un monument mégalithique complexe, dont la fonction (religieuse, astronomique) reste mystérieuse.', '-3000-01-01', 'Wiltshire, Angleterre', 51.18000000, -1.83000000, '["Architecture", "Astronomie", "Religion"]', '["Chippindale, Christopher. Stonehenge Complete. Thames & Hudson, 2004."]', NOW(), NOW()),
(4, 1, 'Maîtrise du feu', 'Découverte et utilisation du feu par les hominidés, révolutionnant la cuisson des aliments, l''éclairage et la défense.', '-400000-01-01', 'Sites variés (ex: Grotte de Wonderwerk, Afrique du Sud ; Grotte de Qesem, Israël)', NULL, NULL, '["Technologie", "Survie", "Évolution"]', '["Gowlett, John A. J. Ascent to Civilization. Collins, 1984."]', NOW(), NOW()),
(5, 1, 'Invention de l''agriculture', 'Transition du mode de vie nomade de chasseurs-cueilleurs à la sédentarisation avec la culture des plantes et l''élevage.', '-10000-01-01', 'Croissant fertile (Moyen-Orient), puis diffusion mondiale', NULL, NULL, '["Agriculture", "Société", "Néolithique"]', '["Diamond, Jared. Guns, Germs, and Steel. W. W. Norton & Company, 1997."]', NOW(), NOW()),
(6, 1, 'Débuts de la poterie', 'Invention de récipients en argile cuite, permettant le stockage et la cuisson des aliments.', '-20000-01-01', 'Sites variés (ex: Grotte de Xianrendong, Chine ; Japon ; Proche-Orient)', NULL, NULL, '["Technologie", "Artisanat", "Néolithique"]', '["Rice, Prudence M. Pottery Analysis: A Sourcebook. University of Chicago Press, 1987."]', NOW(), NOW()),
(7, 1, 'Domestication du chien', 'Apparition du premier animal domestique, jouant un rôle crucial dans la chasse et la protection.', '-15000-01-01', 'Eurasie (origine débattue)', NULL, NULL, '["Domestication", "Animaux", "Paléolithique"]', '["Olsen, Stanley J. Origins of the Domestic Dog. University of Arizona Press, 1985."]', NOW(), NOW()),
(8, 1, 'Premiers outils en pierre taillée', 'Fabrication d''outils rudimentaires, marquant une étape clé dans l''évolution humaine.', '-3300000-01-01', 'Afrique de l''Est (ex: Lomekwi 3, Kenya ; Gona, Éthiopie)', 4.18000000, 35.83000000, '["Technologie", "Paléolithique"]', '["Leakey, Mary. Olduvai Gorge: My Search for Early Man. Collins, 1979."]', NOW(), NOW()),
(9, 1, 'Migration hors d''Afrique', 'Dispersion des premiers humains vers d''autres continents.', '-70000-01-01', 'Depuis l''Afrique vers l''Asie, l''Europe, puis les autres continents', NULL, NULL, '["Migration", "Anthropologie"]', '["Oppenheimer, Stephen. Out of Eden: The Peopling of the World. Constable, 2003."]', NOW(), NOW()),
(10, 1, 'Disparition des dinosaures', 'Extinction massive d''espèces, ouvrant la voie au développement des mammifères.', '-66000000-01-01', 'Impact de Chicxulub (Péninsule du Yucatán, Mexique) et conséquences globales', 21.40000000, -89.52000000, '["Extinction", "Paléontologie"]', '["Alvarez, Walter, et al. \\"Extraterrestrial Cause for the Cretaceous-Tertiary Extinction.\\" Science, vol. 208, no. 4448, 1980, pp. 1095-1108."]', NOW(), NOW()),
(11, 1, 'L''Homme de Néandertal', 'Apparition et disparition d''une espèce humaine proche, contemporaine d''Homo sapiens.', '-40000-01-01', 'Europe et Asie occidentale et centrale', NULL, NULL, '["Anthropologie", "Évolution"]', '["Stringer, Christopher, et Clive Gamble. In Search of the Neanderthals. Thames & Hudson, 1993."]', NOW(), NOW()),
(12, 1, 'Art pariétal de Chauvet', 'Découverte de peintures rupestres paléolithiques exceptionnellement bien conservées.', '-37000-01-01', 'Vallon-Pont-d''Arc, Ardèche, France', 44.39000000, 4.41000000, '["Art", "Préhistoire"]', '["Clottes, Jean, et David Lewis-Williams. The Shamans of Prehistory: Trance, and Magic. Harry N. Abrams, 1998."]', NOW(), NOW()),
(13, 1, 'Premières sépultures', 'Apparition de pratiques funéraires, témoignant de croyances et de rituels complexes.', '-100000-01-01', 'Sites variés (ex: Israël, Europe)', NULL, NULL, '["Archéologie", "Religion"]', '["Riel-Salvatore, Jean, et April Nowell. \\"An Overview of Middle Paleolithic Burial Practices.\\" Journal of Human Evolution, vol. 55, no. 3, 2008, pp. 349-67."]', NOW(), NOW()),
(14, 1, 'Fabrication d''outils d''Os', 'Développement d''outils plus sophistiqués (harpons, aiguilles) à partir d''os, de bois de cervidé et d''ivoire.', '-50000-01-01', 'Sites variés en Afrique, Europe, Asie', NULL, NULL, '["Technologie", "Préhistoire"]', '["White, Randall. Dark Caves, Bright Visions: Life in Ice Age Europe. W. W. Norton & Company, 1986."]', NOW(), NOW()),
(15, 1, 'Premiers vêtements', 'Utilisation de peaux d''animaux et de fibres végétales pour se protéger du froid et des éléments.', '-40000-01-01', 'Probablement dans les régions froides habitées par les hominidés.', NULL, NULL, '["Technologie", "Préhistoire", "Adaptation"]', '["Gilligan, Ian. \\"Ice Age Survival: A Matter of Clothing.\\" Scientific American, vol. 296, no. 2, 2007, pp. 70-77."]', NOW(), NOW()),
(16, 1, 'Construction de huttes', 'Apparition des premières habitations construites par l''homme, allant de simples abris à des structures plus complexes.', '-25000-01-01', 'Sites variés (France, Ukraine, etc.)', 43.70000000, 7.28000000, '["Architecture", "Préhistoire", "Sédentarisation"]', '["Wenke, Robert J. Patterns in Prehistory: Humankind\'s First Three Million Years. Oxford University Press, 1990."]', NOW(), NOW()),
(17, 1, 'Premiers échanges commerciaux', 'Développement de réseaux d''échanges de biens (outils, parures) et de matières premières.', '-25000-01-01', 'Réseaux d''échanges à travers l''Europe, l''Afrique, le Proche-Orient.', NULL, NULL, '["Économie", "Préhistoire", "Société"]', '["Renfrew, Colin, et Paul Bahn. Archaeology: Theories, Methods, and Practice. Thames & Hudson, 2008."]', NOW(), NOW()),
(18, 1, 'Premiers instruments de musique', 'Fabrication de flûtes et potentiellement d''autres instruments (lithophones, rhombes) à partir d''os, d''ivoire et d''autres matériaux.', '-40000-01-01', 'Sites variés (ex: Allemagne, Slovénie)', 48.39000000, 9.77000000, '["Musique", "Préhistoire", "Art"]', '["Morley, Iain. The Prehistory of Music. Oxford University Press, 2013."]', NOW(), NOW()),
(19, 1, 'Premiers pas de l''artisanat (Art mobilier)', 'Création de bijoux (perles), de sculptures (Vénus paléolithiques), de gravures sur os et pierre, et d''autres objets décoratifs ou symboliques.', '-40000-01-01', 'Sites variés en Europe, Asie, Afrique.', NULL, NULL, '["Art", "Préhistoire", "Symbolisme"]', '["Sandars, Nancy K. Prehistoric Art in Europe. Yale University Press, 1985."]', NOW(), NOW()),
(20, 1, 'Fin de la dernière période glaciaire', 'Réchauffement climatique important, entraînant la fonte des calottes glaciaires, la montée du niveau de la mer et des changements environnementaux majeurs.', '-11700-01-01', 'Phénomène global.', NULL, NULL, '["Climat", "Préhistoire", "Environnement"]', '["Ruddiman, William F. Earth\'s Climate: Past and Future. W. H. Freeman and Company, 2001."]', NOW(), NOW()),
(21, 2, 'Couronnement de Charlemagne', 'Sacre de Charlemagne comme empereur d''Occident par le pape Léon III, événement majeur dans la reconstitution de l''Empire en Occident.', '0800-12-25', 'Basilique Saint-Pierre, Rome, États pontificaux (aujourd''hui Italie/Vatican)', 41.90000000, 12.45000000, '["Politique", "Religion", "Empire Carolingien"]', '["Eginhard, Vie de Charlemagne."]', NOW(), NOW()),
(22, 2, 'Bataille de Hastings', 'Bataille décisive qui vit la victoire de Guillaume le Conquérant sur Harold Godwinson, menant à la conquête normande de l''Angleterre.', '1066-10-14', 'Senlac Hill, près de Battle (non loin de Hastings), Sussex, Angleterre', 50.91000000, 0.49000000, '["Histoire", "Militaire", "Conquête Normande"]', '["Orderic Vitalis, Histoire ecclésiastique."]', NOW(), NOW()),
(23, 2, 'Les Croisades', 'Expéditions militaires et religieuses menées par les chrétiens d''Occident, initialement pour reprendre Jérusalem aux musulmans.', '1095-11-27', 'Principalement vers la Terre Sainte (Levant), mais aussi en Europe (contre les Albigeois, en Péninsule Ibérique).', NULL, NULL, '["Religion", "Guerre", "Moyen-Orient", "Relations interculturelles"]', '["Joinville, Histoire de Saint Louis."]', NOW(), NOW()),
(24, 2, 'Construction des grandes cathédrales gothiques', 'Élévation d''édifices religieux emblématiques (Notre-Dame de Paris, Chartres, Reims, Amiens...), symboles de la foi, de la puissance de l''Église et de l''essor des villes.', '1163-01-01', 'France (berceau du gothique), puis diffusion en Europe (Angleterre, Allemagne, Espagne, Italie...).', 48.85000000, 2.35000000, '["Architecture", "Religion", "Art Gothique", "Société"]', '["Duby, Georges. Le Temps des cathédrales. Art et société, 980-1420."]', NOW(), NOW()),
(25, 2, 'La Peste noire', 'Pandémie dévastatrice de peste bubonique qui ravagea l''Europe, l''Asie et l''Afrique du Nord, entraînant une crise démographique, économique et sociale majeure.', '1347-01-01', 'Originaire d''Asie centrale, propagation en Europe via les routes commerciales (ports méditerranéens).', NULL, NULL, '["Histoire", "Médecine", "Société", "Démographie"]', '["Boccace, Giovanni. Décaméron."]', NOW(), NOW()),
(26, 2, 'Guerre de Cent Ans', 'Long conflit intermittent opposant les royaumes de France (Maison de Valois) et d''Angleterre (Maison Plantagenêt puis Lancastre) pour la succession au trône de France.', '1337-01-01', 'Principalement en France et dans les Flandres.', NULL, NULL, '["Histoire", "Politique", "Militaire", "Nationalisme naissant"]', '["Froissart, Jean. Chroniques."]', NOW(), NOW()),
(27, 2, 'Jeanne d''Arc', 'Héroïne française qui, affirmant agir sur ordre divin, joua un rôle déterminant dans la phase finale de la guerre de Cent Ans, notamment lors du siège d''Orléans.', '1429-01-01', 'Née à Domrémy (France), action militaire centrée sur la vallée de la Loire (Orléans), capturée à Compiègne, jugée et brûlée à Rouen.', 49.44000000, 1.10000000, '["Histoire", "Militaire", "Religion", "Figure nationale"]', '["Pernoud, Régine. Jeanne d''Arc par elle-même et ses témoins."]', NOW(), NOW()),
(28, 2, 'Invention de l''imprimerie (par Gutenberg)', 'Mise au point par Johannes Gutenberg de la presse à caractères mobiles métalliques en Europe, une technique révolutionnaire qui permit la diffusion massive et rapide du savoir.', '1450-01-01', 'Mayence, Saint-Empire romain germanique (aujourd''hui Allemagne).', 50.00000000, 8.27000000, '["Technologie", "Culture", "Communication", "Renaissance"]', '["Febvre, Lucien, et Henri-Jean Martin. L\'Apparition du livre."]', NOW(), NOW()),
(29, 2, 'Chute de Constantinople', 'Prise de la capitale de l''Empire byzantin par les armées ottomanes menées par le sultan Mehmed II, marquant la fin de l''Empire romain d''Orient et souvent considérée comme un jalon de la fin du Moyen Âge.', '1453-05-29', 'Constantinople, Empire byzantin (aujourd''hui Istanbul, Turquie).', 41.01000000, 28.98000000, '["Histoire", "Politique", "Religion", "Fin du Moyen Âge", "Expansion ottomane"]', '["Pertusi, Agostino. La Caduta di Costantinopoli."]', NOW(), NOW()),
(30, 2, 'Les Vikings', 'Période d''expansion (raids, commerce, exploration, colonisation) des peuples scandinaves (Norvégiens, Danois, Suédois) en Europe et au-delà.', '0793-01-01', 'Originaires de Scandinavie ; raids, commerce et colonisation en Grande-Bretagne, Irlande, France (Normandie), Russie (Rus\' de Kiev), Islande, Groenland, Amérique du Nord (Vinland).', NULL, NULL, '["Histoire", "Migration", "Guerre", "Commerce", "Exploration"]', '["Musset, Lucien. Les invasions: les vagues germaniques."]', NOW(), NOW()),
(31, 2, 'Le système féodal', 'Organisation sociale, politique et économique hiérarchisée, fondée sur les liens personnels de dépendance (vassalité) entre seigneurs et vassaux, et l''octroi de fiefs.', '0794-01-01', 'Principalement en Europe occidentale.', NULL, NULL, '["Société", "Politique", "Économie"]', '["Bloch, Marc. La Société féodale."]', NOW(), NOW()),
(32, 2, 'L''art roman', 'Style architectural et artistique caractérisé par ses arcs en plein cintre, ses voûtes en berceau ou d''arêtes, ses murs épais et ses sculptures souvent robustes et expressives.', '0900-01-01', 'Europe occidentale (France, Espagne, Italie, Allemagne, Angleterre...).', NULL, NULL, '["Art", "Architecture", "Religion"]', '["Focillon, Henri. L\'Art d''Occident : le Moyen Âge roman."]', NOW(), NOW()),
(33, 2, 'L''Inquisition médiévale', 'Institution ecclésiastique chargée de rechercher, juger et condamner les personnes accusées d''hérésie par l''Église catholique.', '1230-01-01', 'Principalement en Europe catholique (Sud de la France, Italie, Allemagne, Espagne...).', NULL, NULL, '["Religion", "Histoire", "Justice ecclésiastique"]', '["Lea, Henry Charles. A History of the Inquisition of the Middle Ages."]', NOW(), NOW()),
(34, 2, 'Les universités médiévales', 'Création des premières institutions d''enseignement supérieur autonomes en Europe, issues souvent d''écoles cathédrales ou monastiques.', '1088-01-01', 'Grandes villes d''Europe (Bologne, Paris, Oxford, Cambridge, Salamanque, Montpellier...).', 48.85000000, 2.35000000, '["Éducation", "Société", "Culture", "Savoir"]', '["Le Goff, Jacques. Les intellectuels au Moyen Âge."]', NOW(), NOW()),
(35, 2, 'Les communes médiévales', 'Mouvements d''émancipation politique et juridique des bourgeois des villes vis-à-vis du pouvoir seigneurial (laïc ou ecclésiastique), aboutissant à l''obtention de chartes de franchises.', '1000-01-01', 'Villes d''Europe occidentale (Italie du Nord, Flandres, France, Allemagne...).', NULL, NULL, '["Société", "Politique", "Urbanisation", "Bourgeoisie"]', '["Pirenne, Henri. Les villes du Moyen Âge."]', NOW(), NOW()),
(36, 2, 'L''alchimie médiévale', 'Pratique proto-scientifique, philosophique et mystique visant notamment la transmutation des métaux (en or), la découverte de la panacée et de l''élixir de longue vie.', '1000-11-01', 'Europe, Monde arabe.', NULL, NULL, '["Science (proto)", "Philosophie", "Ésotérisme"]', '["Eliade, Mircea. Forgerons et alchimistes."]', NOW(), NOW()),
(37, 2, 'Les guildes (ou corporations)', 'Associations professionnelles regroupant artisans et/ou commerçants d''un même métier dans une ville, réglementant la production, la formation (apprentissage) et la vente.', '1000-01-01', 'Villes d''Europe.', NULL, NULL, '["Économie", "Société", "Artisanat", "Commerce"]', '["Le Goff, Jacques. Métiers et Marchands au Moyen Âge."]', NOW(), NOW()),
(38, 2, 'La musique médiévale', 'Développement de formes musicales variées, allant du chant monodique sacré (grégorien) à la polyphonie complexe (motets, messes), en passant par la musique profane des troubadours et trouvères.', '0500-01-01', 'Europe.', NULL, NULL, '["Musique", "Religion", "Culture"]', '["Hoppin, Richard H. Medieval Music."]', NOW(), NOW()),
(39, 2, 'Les châteaux forts', 'Construction de fortifications résidentielles massives pour protéger les seigneurs, leur famille et leurs biens, servant de centres de pouvoir et de défense territoriale.', '1000-01-01', 'Europe, Proche-Orient (châteaux croisés).', 49.24000000, 1.40000000, '["Architecture", "Militaire", "Société féodale"]', '["Viollet-le-Duc, Eugène. Dictionnaire raisonné de l''architecture française du XIe au XVIe siècle."]', NOW(), NOW()),
(40, 2, 'Les ordres monastiques', 'Fondation et essor de communautés religieuses (moines, moniales) vivant selon une règle spécifique, jouant un rôle majeur dans la vie spirituelle, intellectuelle (scriptoria), économique (défrichement) et sociale.', '0529-01-01', 'Europe.', 46.43000000, 4.66000000, '["Religion", "Société", "Culture", "Économie"]', '["Knowles, David. The Monastic Order in England."]', NOW(), NOW()),
(41, 3, 'Découverte de l''Amérique (par Christophe Colomb)', 'Arrivée de Christophe Colomb, naviguant pour la couronne espagnole, sur le continent américain (qu''il croit être les Indes), événement majeur ouvrant la voie à l''exploration et la colonisation européennes des Amériques ("Nouveau Monde").', '1492-10-12', 'Départ : Palos de la Frontera (Espagne) ; Arrivée : Île de San Salvador (Bahamas), puis Cuba, Hispaniola.', 24.04000000, -74.53000000, '["Exploration", "Colonisation", "Histoire", "Échanges colombiens"]', '["Todorov, Tzvetan. La Conquête de l''Amérique : la question de l''autre. Seuil, 1982."]', NOW(), NOW()),
(42, 3, 'Début de la Réforme protestante', 'Affichage par Martin Luther de ses 95 thèses critiquant les indulgences, acte considéré comme le début d''un mouvement religieux (protestantisme) qui brisa l''unité de la chrétienté occidentale et bouleversa l''Europe.', '1517-10-31', 'Wittenberg, Saxe, Saint-Empire romain germanique (aujourd''hui Allemagne).', 51.87000000, 12.65000000, '["Religion", "Politique", "Société", "Théologie"]', '["Febvre, Lucien. Martin Luther. PUF, 1928."]', NOW(), NOW()),
(43, 3, 'Renaissance italienne', 'Période d''effervescence artistique, intellectuelle et culturelle majeure, marquée par le retour aux modèles de l''Antiquité gréco-romaine, l''humanisme, et l''émergence de génies universels.', '1400-01-01', 'Italie (Florence, Rome, Venise, Milan...).', 43.77000000, 11.25000000, '["Art", "Culture", "Philosophie", "Humanisme", "Science"]', '["Burckhardt, Jacob. La Civilisation de la Renaissance en Italie."]', NOW(), NOW()),
(44, 3, 'Concile de Trente', 'Concile œcuménique de l''Église catholique convoqué pour répondre à la Réforme protestante (Contre-Réforme), réaffirmant les dogmes catholiques et réformant la discipline ecclésiastique.', '1545-01-01', 'Trente, Principauté épiscopale de Trente (aujourd''hui en Italie).', 46.07000000, 11.12000000, '["Religion", "Politique", "Histoire", "Contre-Réforme"]', '["Jedin, Hubert. Histoire du Concile de Trente."]', NOW(), NOW()),
(45, 3, 'Guerres de Religion en France', 'Série de conflits civils opposant catholiques et protestants (huguenots) en France, marqués par des massacres (Saint-Barthélemy, 1572) et des luttes politiques complexes.', '1562-03-01', 'Royaume de France.', NULL, NULL, '["Histoire", "Religion", "Politique", "Guerre civile"]', '["Crouzet, Denis. Les Guerriers de Dieu. La violence au temps des troubles de religion (v. 1525-v. 1610)."]', NOW(), NOW()),
(46, 3, 'Édit de Nantes', 'Décret royal promulgué par Henri IV pour mettre fin aux guerres de Religion, accordant aux protestants français (huguenots) des droits substantiels dans une nation considérée comme essentiellement catholique (liberté de conscience, liberté de culte limitée, places de sûreté). Révoqué par Louis XIV en 1685.', '1598-04-13', 'Nantes, France.', 47.22000000, -1.55000000, '["Histoire", "Religion", "Politique", "Tolérance"]', '["Garrisson, Janine. L\'Édit de Nantes. Histoire d\'une tolérance."]', NOW(), NOW()),
(47, 3, 'Fondation de la Compagnie de Jésus (Jésuites)', 'Création d''un ordre religieux catholique masculin dont les membres sont appelés Jésuites. L''ordre joua un rôle majeur dans la Contre-Réforme, l''éducation et les missions d''évangélisation dans le monde entier.', '1540-09-27', 'Fondation à Paris, approbation à Rome.', 41.90000000, 12.45000000, '["Religion", "Histoire", "Éducation", "Missions", "Contre-Réforme"]', '["Bangert, William V. A History of the Society of Jesus."]', NOW(), NOW()),
(48, 3, 'Révolution copernicienne', 'Changement fondamental dans la conception de l''univers, passant du modèle géocentrique (Terre au centre) de Ptolémée au modèle héliocentrique (Soleil au centre) proposé par Copernic, et développé ensuite par Kepler, Galilée et Newton.', '1543-01-01', 'Frombork (Frauenburg), Prusse royale (aujourd''hui Pologne), où Copernic a travaillé.', 54.36000000, 19.68000000, '["Science", "Astronomie", "Philosophie", "Révolution scientifique"]', '["Kuhn, Thomas S. La Révolution copernicienne."]', NOW(), NOW()),
(49, 3, 'Voyages d''exploration (Grandes Découvertes)', 'Période d''intense exploration maritime menée par les Européens, conduisant à la découverte de nouvelles routes maritimes, de nouveaux continents et territoires, et à l''établissement de premiers empires coloniaux.', '1400-01-01', 'Initiés par le Portugal et l''Espagne, puis rejoints par l''Angleterre, la France, les Pays-Bas. Exploration des côtes africaines, traversée de l''Atlantique (Amériques), de l''Océan Indien, premier tour du monde (Magellan-Elcano, 1519-1522).', NULL, NULL, '["Histoire", "Géographie", "Science", "Exploration", "Colonisation"]', '["Chaunu, Pierre. L\'Expansion européenne du XIIIe au XVe siècle."]', NOW(), NOW()),
(50, 3, 'Léonard de Vinci', 'Artiste (peintre de La Joconde, La Cène), scientifique, inventeur, anatomiste, ingénieur... Incarnation de l''idéal de l''homme universel de la Renaissance.', '1452-04-15', 'Né à Vinci (Italie), actif à Florence, Milan, Rome, puis en France (Amboise).', 43.79000000, 10.93000000, '["Art", "Science", "Ingénierie", "Anatomie"]', '["Vasari, Giorgio. Les Vies des meilleurs peintres, sculpteurs et architectes."]', NOW(), NOW()),
(51, 3, 'Michel-Ange', 'Sculpteur (David, Pietà), peintre (plafond de la Chapelle Sixtine), architecte (Dôme de Saint-Pierre) et poète majeur de la Haute Renaissance italienne.', '1475-03-06', 'Né près de Florence, actif principalement à Florence et Rome.', 41.90000000, 12.45000000, '["Art", "Sculpture", "Peinture", "Architecture"]', '["Condivi, Ascanio. Vie de Michel-Ange."]', NOW(), NOW()),
(52, 3, 'Shakespeare', 'Dramaturge, poète et acteur anglais, considéré comme l''un des plus grands écrivains de langue anglaise et une figure majeure de la littérature mondiale (Hamlet, Roméo et Juliette, Macbeth...).', '1564-04-26', 'Né à Stratford-upon-Avon, actif principalement à Londres, Angleterre.', 51.51000000, -0.13000000, '["Littérature", "Théâtre", "Poésie", "Renaissance anglaise"]', '["Greenblatt, Stephen. Will in the World: How Shakespeare Became Shakespeare."]', NOW(), NOW()),
(53, 3, 'Galilée (Galileo Galilei)', 'Astronome, physicien et ingénieur italien, pionnier de la méthode scientifique expérimentale. Ses observations astronomiques (phases de Vénus, lunes de Jupiter) soutinrent l''héliocentrisme copernicien, ce qui le conduisit à un conflit avec l''Église catholique.', '1564-02-15', 'Né à Pise, actif à Pise, Padoue, Florence (Italie).', 43.77000000, 11.25000000, '["Science", "Astronomie", "Physique", "Révolution scientifique"]', '["Drake, Stillman. Galileo."]', NOW(), NOW()),
(54, 3, 'La Contre-Réforme (ou Réforme catholique)', 'Mouvement de renouveau interne et de réaction de l''Église catholique face à la Réforme protestante, visant à réaffirmer la doctrine catholique, réformer les abus, renforcer l''autorité papale et reconquérir les fidèles.', '1545-01-01', 'Pays catholiques d''Europe.', NULL, NULL, '["Religion", "Histoire", "Art (Baroque)"]', '["Dickens, A. G. The Counter Reformation."]', NOW(), NOW()),
(55, 3, 'L''humanisme', 'Mouvement intellectuel, littéraire et philosophique qui place l''homme et les valeurs humaines (raison, liberté, dignité) au centre de la réflexion, en s''inspirant des textes et de la culture de l''Antiquité gréco-romaine. Figures : Pétrarque, Érasme, Thomas More...', '1300-01-01', 'Italie, puis France, Pays-Bas, Angleterre, Allemagne...', NULL, NULL, '["Philosophie", "Culture", "Littérature", "Éducation"]', '["Nauert, Charles G. Humanism and the Culture of Renaissance Europe."]', NOW(), NOW()),
(56, 3, 'La cartographie', 'Développement de techniques de représentation du monde (cartes marines ou portulans, planisphères) de plus en plus précises, stimulé par les grandes découvertes et l''imprimerie. Figures : Mercator, Ortelius.', '1400-01-01', 'Centres de cartographie : Portugal, Espagne, Italie, Pays-Bas (Anvers, Amsterdam).', NULL, NULL, '["Science", "Géographie", "Exploration", "Technologie"]', '["Woodward, David. The History of Cartography."]', NOW(), NOW()),
(57, 3, 'La musique de la Renaissance', 'Période marquée par l''apogée de la polyphonie vocale (messe, motet, madrigal, chanson), une plus grande expressivité, et le développement de la musique instrumentale. Compositeurs : Josquin des Prés, Palestrina, Lassus, Monteverdi (transition vers le Baroque).', '1400-01-01', 'Europe (École franco-flamande, Italie, Angleterre, Espagne...).', NULL, NULL, '["Musique", "Art", "Culture"]', '["Reese, Gustave. Music in the Renaissance."]', NOW(), NOW()),
(58, 3, 'La mode de la Renaissance', 'Évolution des costumes et des accessoires, reflétant les changements sociaux, économiques (nouveaux matériaux) et culturels (idéal de magnificence). Caractéristiques : vêtements amples puis plus ajustés, fraises, pourpoints, vertugadins, richesse des tissus et des ornements.', '1400-01-01', 'Europe (influences italiennes, espagnoles, allemandes, anglaises...).', NULL, NULL, '["Art", "Société", "Culture", "Mode"]', '["Ribeiro, Aileen. Fashion and Morality."]', NOW(), NOW()),
(59, 3, 'Le théâtre élisabéthain', 'Période d''efflorescence majeure du théâtre anglais, avec des dramaturges comme William Shakespeare, Christopher Marlowe, Ben Jonson. Caractérisée par des théâtres spécifiques, un public varié et des pièces mêlant tragédie, comédie et histoire.', '1576-01-01', 'Angleterre, principalement Londres (théâtres comme Le Globe).', 51.51000000, -0.13000000, '["Art", "Littérature", "Théâtre", "Culture anglaise"]', '["Gurr, Andrew. The Shakespearean Stage, 1574-1642."]', NOW(), NOW()),
(60, 4, 'Traité de Westphalie', 'Série de traités mettant fin à la guerre de Trente Ans (Saint-Empire) et à la guerre de Quatre-Vingts Ans (Espagne-Provinces-Unies). Ils redessinent la carte politique de l''Europe et établissent les principes de souveraineté des États et d''équilibre des puissances.', '1648-10-24', 'Münster et Osnabrück, Westphalie (Saint-Empire romain germanique, aujourd''hui Allemagne).', 51.96000000, 7.63000000, '["Histoire", "Politique", "Diplomatie", "Relations internationales"]', '["Osiander, Andreas. Before the State."]', NOW(), NOW()),
(61, 4, 'Siècle des Lumières', 'Mouvement intellectuel, culturel et philosophique prônant la primauté de la raison, la liberté de pensée, la critique de l''absolutisme et des dogmes religieux, la diffusion des connaissances (Encyclopédie) et la recherche du bonheur et du progrès. Figures : Voltaire, Rousseau, Montesquieu, Diderot, Kant, Locke, Hume...', '1715-01-01', 'Mouvement européen, centré sur la France, mais aussi important en Grande-Bretagne, Allemagne, Italie, etc.', NULL, NULL, '["Philosophie", "Science", "Société", "Politique", "Culture"]', '["Cassirer, Ernst. La Philosophie des Lumières."]', NOW(), NOW()),
(62, 4, 'Révolution américaine', 'Lutte politique puis militaire des colonies britanniques d''Amérique du Nord pour obtenir leur indépendance de la Grande-Bretagne, menant à la création des États-Unis d''Amérique, une république fondée sur des principes constitutionnels et démocratiques (pour l''époque).', '1776-07-04', 'Treize colonies britanniques d''Amérique du Nord (futurs États-Unis).', 39.95000000, -75.16000000, '["Histoire", "Politique", "Indépendance", "Révolution", "Démocratie"]', '["Bailyn, Bernard. The Ideological Origins of the American Revolution."]', NOW(), NOW()),
(63, 4, 'Révolution française', 'Période de bouleversements sociaux et politiques majeurs en France, marquant la fin de l''Ancien Régime (monarchie absolue, société d''ordres) et l''avènement de la République, avec des répercussions profondes en Europe et dans le monde.', '1789-07-14', 'France (principalement Paris et les grandes villes).', 48.86000000, 2.35000000, '["Histoire", "Politique", "Société", "Révolution", "Droits de l''homme"]', '["Michelet, Jules. Histoire de la Révolution française."]', NOW(), NOW()),
(64, 5, 'Empire napoléonien', 'Période de domination politique et militaire de Napoléon Bonaparte sur la France et une grande partie de l''Europe, caractérisée par des guerres quasi-continuelles (Guerres napoléoniennes), des réformes administratives et juridiques (Code civil) et la diffusion des idées révolutionnaires.', '1804-12-02', 'France et une grande partie de l''Europe continentale (territoires annexés, États vassaux).', 48.86000000, 2.35000000, '["Histoire", "Politique", "Militaire", "Guerres napoléoniennes"]', '["Tulard, Jean. Napoléon."]', NOW(), NOW()),
(65, 5, 'Congrès de Vienne', 'Conférence diplomatique réunissant les puissances européennes victorieuses de Napoléon Ier pour redessiner la carte politique de l''Europe après les guerres napoléoniennes, restaurer les monarchies et tenter d''établir un équilibre durable des puissances.', '1814-09-01', 'Vienne, Autriche.', 48.21000000, 16.36000000, '["Histoire", "Politique", "Diplomatie", "Restauration", "Relations internationales"]', '["Kissinger, Henry. A World Restored."]', NOW(), NOW()),
(66, 4, 'Révolutions industrielles', 'Ensemble de transformations économiques et sociales profondes liées à l''innovation technologique (machine à vapeur, charbon, fer, puis électricité, pétrole, acier, chimie), au développement de l''industrie (usines), des transports (chemin de fer) et à l''urbanisation.', '1760-01-01', 'Début en Grande-Bretagne, puis diffusion en Europe occidentale, Amérique du Nord, Japon.', 53.48000000, -2.24000000, '["Économie", "Technologie", "Société", "Urbanisation", "Capitalisme"]', '["Landes, David S. The Unbound Prometheus."]', NOW(), NOW()),
(67, 5, 'Unification italienne (Risorgimento)', 'Mouvement politique et militaire visant à unifier les différents États de la péninsule italienne en un seul État-nation, mené principalement par le royaume de Piémont-Sardaigne (Cavour, Victor-Emmanuel II) avec l''aide de figures comme Garibaldi.', '1848-01-01', 'Péninsule italienne.', 41.90000000, 12.49000000, '["Histoire", "Politique", "Nationalisme", "Unification"]', '["Riall, Lucy. The Italian Risorgimento."]', NOW(), NOW()),
(68, 5, 'Guerre de Sécession américaine', 'Guerre civile opposant les États de l''Union (Nord), dirigés par Abraham Lincoln, aux États confédérés d''Amérique (Sud), qui avaient fait sécession principalement pour préserver l''institution de l''esclavage. La victoire de l''Union a préservé l''unité du pays et aboli l''esclavage.', '1861-04-12', 'États-Unis d''Amérique.', 38.90000000, -77.04000000, '["Histoire", "Politique", "Société", "Esclavage", "Guerre civile"]', '["McPherson, James M. Battle Cry of Freedom."]', NOW(), NOW()),
(69, 5, 'Colonisation de l''Afrique ("Partage de l''Afrique")', 'Période d''invasion, d''occupation, de division et de colonisation de la quasi-totalité du continent africain par les puissances européennes (Grande-Bretagne, France, Belgique, Portugal, Allemagne, Italie, Espagne) durant la période du Nouvel Impérialisme.', '1884-11-15', 'Continent africain.', NULL, NULL, '["Histoire", "Politique", "Impérialisme", "Colonisation", "Relations Nord-Sud"]', '["Hochschild, Adam. King Leopold\'s Ghost."]', NOW(), NOW()),
(70, 4, 'Traite négrière transatlantique', 'Déportation forcée de millions d''Africains réduits en esclavage vers les colonies européennes des Amériques pour travailler principalement dans les plantations (sucre, coton, café...).', '1500-01-01', 'Commerce triangulaire entre l''Europe, l''Afrique (côtes occidentales principalement) et les Amériques.', NULL, NULL, '["Esclavage", "Histoire", "Commerce", "Crime contre l''humanité"]', '["Blackburn, Robin. The Making of New World Slavery."]', NOW(), NOW()),
(71, 5, 'Indépendance de Haïti', 'Révolte victorieuse des esclaves et des affranchis de la colonie française de Saint-Domingue contre le pouvoir colonial et l''esclavage, menant à la création de la première république noire indépendante et du premier État indépendant d''Amérique latine. Figures : Toussaint Louverture, Jean-Jacques Dessalines.', '1804-01-01', 'Colonie française de Saint-Domingue (île d''Hispaniola).', 18.54000000, -72.34000000, '["Esclavage", "Révolution", "Indépendance", "Décolonisation"]', '["Dubois, Laurent. Avengers of the New World."]', NOW(), NOW()),
(72, 5, 'Abolition de l''esclavage', 'Mouvements politiques et sociaux, et processus législatifs visant à interdire et mettre fin à l''institution de l''esclavage et à la traite négrière dans différentes parties du monde.', '1833-01-01', 'Mouvements abolitionnistes en Europe et aux Amériques ; législations dans les pays concernés.', NULL, NULL, '["Esclavage", "Droits de l''homme", "Abolitionnisme", "Histoire sociale"]', '["Drescher, Seymour. Abolitionism: A Very Short Introduction."]', NOW(), NOW()),
(73, 5, 'Conquête de l''Ouest américain', 'Expansion territoriale des États-Unis vers l''ouest du continent nord-américain, marquée par l''acquisition de terres (Achat de la Louisiane, Cession mexicaine), la migration de colons, les guerres indiennes, la ruée vers l''or et la construction du chemin de fer transcontinental.', '1803-01-01', 'Ouest des États-Unis (des Appalaches à la côte Pacifique).', NULL, NULL, '["Histoire", "Territoire", "Expansion", "Migration", "Relations avec les Amérindiens"]', '["Limerick, Patricia Nelson. The Legacy of Conquest."]', NOW(), NOW()),
(74, 5, 'Industrialisation de l''Europe', 'Diffusion et développement de l''industrie manufacturière (textile, métallurgie, charbonnage...) en Europe continentale, suivant le modèle britannique, entraînant urbanisation, croissance économique, transformations sociales et apparition de la classe ouvrière.', '1800-01-01', 'Grande-Bretagne, Belgique, France, Allemagne, puis reste de l''Europe à des degrés divers.', NULL, NULL, '["Économie", "Technologie", "Société", "Urbanisation"]', '["Hobsbawm, Eric J. The Age of Revolution: 1789-1848."]', NOW(), NOW()),
(75, 5, 'Nationalisme', 'Idéologie politique et mouvement social prônant la primauté de la nation (groupe partageant une identité commune : langue, culture, histoire) comme base de l''organisation politique, visant souvent à l''autodétermination (création d''un État-nation) ou à l''expansion.', '1848-01-01', 'Principalement en Europe, puis diffusion mondiale.', NULL, NULL, '["Politique", "Société", "Idéologie", "État-nation"]', '["Anderson, Benedict. Imagined Communities."]', NOW(), NOW()),
(76, 5, 'Impérialisme (Nouvel Impérialisme)', 'Politique d''expansion territoriale, de domination politique, économique et culturelle exercée par les grandes puissances industrielles sur d''autres peuples et territoires, conduisant à la formation de vastes empires coloniaux.', '1870-01-01', 'Domination exercée par les puissances européennes, les États-Unis et le Japon sur de vastes territoires en Afrique, Asie et Océanie.', NULL, NULL, '["Politique", "Histoire", "Économie", "Colonisation", "Relations internationales"]', '["Said, Edward W. Culture and Imperialism."]', NOW(), NOW()),
(77, 5, 'Mouvements ouvriers', 'Ensemble des luttes et organisations (syndicats, partis politiques socialistes/communistes) visant à défendre les droits et améliorer les conditions de vie et de travail de la classe ouvrière face au capitalisme industriel (revendications sur les salaires, le temps de travail, la sécurité, droits politiques...).', '1800-01-01', 'Pays industrialisés (Europe, Amérique du Nord).', NULL, NULL, '["Politique", "Société", "Socialisme", "Syndicalisme", "Lutte des classes"]', '["Thompson, E. P. The Making of the English Working Class."]', NOW(), NOW()),
(78, 5, 'Suffragettes', 'Militantes luttant pour l''obtention du droit de vote pour les femmes (suffrage féminin), utilisant parfois des méthodes d''action directe et de désobéissance civile.', '1890-01-01', 'Principalement au Royaume-Uni et aux États-Unis, mais mouvements similaires dans d''autres pays.', 51.51000000, -0.13000000, '["Politique", "Société", "Féminisme", "Droits des femmes"]', '["Pankhurst, Emmeline. Suffragette."]', NOW(), NOW()),
(79, 5, 'Première vague de mondialisation', 'Période d''intensification sans précédent des échanges économiques (commerce international, flux de capitaux), des migrations humaines et de la circulation des idées à l''échelle mondiale, favorisée par les progrès technologiques (transports, communication) et la stabilité relative (étalon-or, Pax Britannica).', '1870-01-01', 'Phénomène global centré sur l''Europe et l''Amérique du Nord.', NULL, NULL, '["Économie", "Société", "Mondialisation", "Migrations", "Technologie"]', '["Osterhammel, Jürgen. The Transformation of the World: A Global History of the Nineteenth Century."]', NOW(), NOW()),
(80, 5, 'Première Guerre mondiale', 'Conflit militaire majeur impliquant les Puissances Centrales (Allemagne, Autriche-Hongrie, Empire Ottoman, Bulgarie) contre les Alliés (France, Royaume-Uni, Russie, Italie, États-Unis, etc.). Caractérisé par la guerre de tranchées, l''utilisation de nouvelles armes et des pertes humaines massives.', '1914-07-28', 'Principalement en Europe (Front Ouest, Front Est, Balkans, Italie), mais aussi au Moyen-Orient, en Afrique, et sur les mers.', NULL, NULL, '["Histoire", "Guerre", "Politique", "Relations internationales", "Technologie militaire"]', '["Winter, J. M. The First World War."]', NOW(), NOW()),
(81, 5, 'Traité de Versailles', 'Principal traité de paix mettant officiellement fin à la Première Guerre mondiale entre l''Allemagne et les puissances alliées. Il imposa de lourdes conditions à l''Allemagne (pertes territoriales, réparations financières, restrictions militaires), souvent considérées comme une source de ressentiment ayant contribué à la montée du nazisme et à la Seconde Guerre mondiale.', '1919-06-28', 'Galerie des Glaces, Château de Versailles, France.', 48.80000000, 2.12000000, '["Histoire", "Politique", "Diplomatie", "Relations internationales", "Conséquences de la WWI"]', '["Macmillan, Margaret. Paris 1919."]', NOW(), NOW()),
(82, 5, 'Révolution russe', 'Série de révolutions qui mirent fin au régime tsariste autocratique et conduisirent à la création de la République socialiste fédérative soviétique de Russie, puis de l''Union des républiques socialistes soviétiques (URSS) en 1922, premier État communiste de l''histoire.', '1917-03-08', 'Empire russe (principalement Petrograd et Moscou).', 59.93000000, 30.34000000, '["Histoire", "Politique", "Société", "Révolution", "Communisme"]', '["Fitzpatrick, Sheila. The Russian Revolution."]', NOW(), NOW()),
(83, 5, 'Seconde Guerre mondiale', 'Conflit militaire le plus vaste et le plus meurtrier de l''histoire, opposant les Alliés (Royaume-Uni, France, URSS, États-Unis, Chine, etc.) aux puissances de l''Axe (Allemagne nazie, Italie fasciste, Japon impérial). Marqué par des crimes de guerre massifs, le génocide des Juifs (Shoah) et l''utilisation de l''arme atomique.', '1939-09-01', 'Conflit mondial impliquant la majorité des nations du monde, principaux théâtres d''opérations en Europe, Afrique du Nord, Asie de l''Est et Pacifique.', NULL, NULL, '["Histoire", "Guerre", "Politique", "Génocide", "Totalitarisme"]', '["Beevor, Antony. The Second World War."]', NOW(), NOW()),
(84, 5, 'Shoah (Holocauste)', 'Génocide systématique perpétré par le régime nazi et ses collaborateurs visant à anéantir les Juifs d''Europe, entraînant la mort d''environ six millions de personnes. D''autres groupes (Roms, homosexuels, handicapés, opposants politiques) furent également persécutés et assassinés.', '1941-01-01', 'Territoires contrôlés par l''Allemagne nazie et ses alliés en Europe. Camps d''extermination principaux en Pologne occupée (Auschwitz-Birkenau, Treblinka, Sobibor...).', 50.03000000, 19.20000000, '["Histoire", "Génocide", "Droits de l''homme", "Nazisme", "Antisémitisme"]', '["Levi, Primo. Si c\'est un homme."]', NOW(), NOW()),
(85, 5, 'Bombes atomiques sur Hiroshima et Nagasaki', 'Utilisation de l''arme nucléaire par les États-Unis contre les villes japonaises d''Hiroshima ("Little Boy") et Nagasaki ("Fat Man") à la fin de la Seconde Guerre mondiale, provoquant des destructions massives et des centaines de milliers de morts immédiates et différées. Ces bombardements précipitèrent la capitulation du Japon et marquèrent l''entrée dans l''ère nucléaire.', '1945-08-06', 'Hiroshima et Nagasaki, Japon.', 34.39000000, 132.45000000, '["Histoire", "Guerre", "Technologie", "Arme nucléaire", "Éthique"]', '["Rhodes, Richard. The Making of the Atomic Bomb."]', NOW(), NOW()),
(86, 5, 'Guerre froide', 'Période de tensions géopolitiques, idéologiques et militaires intenses entre les États-Unis et leurs alliés (bloc capitaliste/OTAN) et l''Union soviétique et ses alliés (bloc communiste/Pacte de Varsovie), sans affrontement militaire direct à grande échelle entre les deux superpuissances, mais marquée par la course aux armements (nucléaires), des guerres par procuration, l''espionnage et la compétition technologique (course à l''espace).', '1947-01-01', 'Confrontation globale indirecte entre les blocs de l''Ouest (mené par les États-Unis) et de l''Est (mené par l''URSS). Points chauds : Berlin, Corée, Cuba, Vietnam, Afghanistan...', NULL, NULL, '["Histoire", "Politique", "Idéologie", "Relations internationales", "Course aux armements"]', '["Gaddis, John Lewis. The Cold War."]', NOW(), NOW()),
(87, 5, 'Chute du mur de Berlin', 'Événement majeur et symbolique marquant l''ouverture de la frontière entre Berlin-Est et Berlin-Ouest, précipitant l''effondrement du régime est-allemand, la réunification de l''Allemagne (1990) et la fin de la Guerre froide en Europe.', '1989-11-09', 'Berlin, Allemagne.', 52.52000000, 13.38000000, '["Histoire", "Politique", "Société", "Guerre froide", "Réunification allemande"]', '["Taylor, Frederick. The Berlin Wall."]', NOW(), NOW()),
(88, 5, 'Attentats du 11 septembre 2001', 'Série de quatre attentats-suicides coordonnés perpétrés par le groupe terroriste islamiste Al-Qaïda, utilisant des avions de ligne détournés. Les attaques contre le World Trade Center et le Pentagone firent près de 3000 morts et eurent des répercussions majeures sur la politique intérieure et étrangère américaine ("Guerre contre le terrorisme") et les relations internationales.', '2001-09-11', 'New York (World Trade Center), Arlington, Virginie (Pentagone), Shanksville, Pennsylvanie (crash du vol 93), États-Unis.', 40.71000000, -74.01000000, '["Histoire", "Politique", "Terrorisme", "Relations internationales"]', '["Wright, Lawrence. The Looming Tower."]', NOW(), NOW()),
(89, 5, 'Pandémie de COVID-19', 'Crise sanitaire mondiale provoquée par le coronavirus SARS-CoV-2, entraînant des millions de morts, des perturbations économiques majeures (confinements, récessions), des bouleversements sociaux et une accélération de la recherche médicale (vaccins).', '2020-03-11', 'Origine à Wuhan (Chine), puis diffusion mondiale.', NULL, NULL, '["Histoire", "Médecine", "Société", "Économie", "Santé publique"]', '["Zimmer, Carl. A Planet of Viruses.", "Rapports de l\'OMS."]', NOW(), NOW()),
(90, 5, 'Création de l''ONU (Organisation des Nations Unies)', 'Fondation d''une organisation internationale visant à maintenir la paix et la sécurité internationales, développer des relations amicales entre les nations, promouvoir la coopération internationale et servir de centre où s''harmonisent les efforts des nations. Créée après la Seconde Guerre mondiale pour remplacer la Société des Nations.', '1945-10-24', 'Signature de la Charte à San Francisco (États-Unis) ; Siège principal à New York.', 40.75000000, -73.97000000, '["Politique", "Histoire", "Relations internationales", "Droit international", "Paix"]', '["Meisler, Stanley. United Nations: The First Fifty Years."]', NOW(), NOW()),
(91, 5, 'Décolonisation', 'Processus politique par lequel les colonies acquièrent leur indépendance vis-à-vis de la puissance coloniale (métropole). Ce processus fut parfois pacifique, mais souvent marqué par des luttes politiques, des mouvements nationalistes et des guerres d''indépendance.', '1945-01-01', 'Empires coloniaux européens en Asie (Inde, Indonésie, Indochine...) et en Afrique (Afrique du Nord, Afrique subsaharienne...).', NULL, NULL, '["Politique", "Histoire", "Décolonisation", "Nationalisme", "Relations Nord-Sud"]', '["Fanon, Frantz. Les Damnés de la terre."]', NOW(), NOW()),
(92, 5, 'Mouvement des droits civiques aux États-Unis', 'Lutte menée principalement par les Afro-Américains pour mettre fin à la ségrégation raciale institutionnalisée ("lois Jim Crow") et obtenir l''égalité des droits civiques et politiques. Mouvement marqué par la désobéissance civile non violente (boycotts, sit-ins) et des figures comme Martin Luther King Jr. et Rosa Parks. Aboutit à des lois majeures comme le Civil Rights Act (1964) et le Voting Rights Act (1965).', '1954-01-01', 'États-Unis, particulièrement dans les États du Sud.', 38.90000000, -77.04000000, '["Politique", "Société", "Droits de l''homme", "Racisme", "Non-violence"]', '["King, Martin Luther Jr. Letter from Birmingham Jail."]', NOW(), NOW()),
(93, 5, 'Guerre du Vietnam', 'Conflit majeur de la Guerre froide opposant le Nord-Vietnam communiste (soutenu par l''URSS et la Chine) et le Front national de libération du Sud-Vietnam (Viêt Cong) au Sud-Vietnam (soutenu principalement par les États-Unis). Guerre longue, coûteuse et très médiatisée, elle s''est soldée par la victoire du Nord et la réunification du Vietnam sous un régime communiste, et a profondément divisé la société américaine.', '1955-11-01', 'Vietnam (Nord et Sud), Laos, Cambodge.', 10.78000000, 106.69000000, '["Histoire", "Politique", "Guerre", "Guerre froide", "Décolonisation (initialement)"]', '["Karnow, Stanley. Vietnam: A History."]', NOW(), NOW()),
(94, 5, 'Révolution culturelle en Chine', 'Mouvement social et politique de masse lancé par Mao Zedong pour, officiellement, purger le Parti communiste chinois des éléments "bourgeois" et "révisionnistes" et raviver l''esprit révolutionnaire. Elle entraîna une période de chaos, de purges politiques, de persécutions, de destructions culturelles et de culte de la personnalité autour de Mao.', '1966-05-16', 'République populaire de Chine.', 39.90000000, 116.41000000, '["Politique", "Société", "Communisme", "Maoïsme", "Histoire chinoise"]', '["Meisner, Maurice. Mao\'s China and After."]', NOW(), NOW()),
(95, 5, 'Chute de l''URSS', 'Processus aboutissant à la dissolution de l''Union des républiques socialistes soviétiques en quinze républiques indépendantes. Cet événement marqua la fin de la Guerre froide, la disparition du bloc communiste en Europe de l''Est et une transformation majeure de l''ordre géopolitique mondial.', '1991-12-26', 'Union soviétique.', 55.75000000, 37.62000000, '["Histoire", "Politique", "Guerre froide", "Communisme", "Relations internationales"]', '["Kotkin, Stephen. Armageddon Averted: The Soviet Collapse, 1970-2000."]', NOW(), NOW()),
(96, 5, 'Mondialisation (accélération contemporaine)', 'Intensification et accélération des échanges économiques (libéralisation, flux financiers), culturels (internet, médias), sociaux et politiques à l''échelle mondiale, caractérisée par une interdépendance croissante entre les pays et les sociétés.', '1980-01-01', 'Phénomène global.', NULL, NULL, '["Économie", "Société", "Culture", "Politique", "Technologie"]', '["Friedman, Thomas L. The World Is Flat."]', NOW(), NOW()),
(97, 5, 'Révolution numérique', 'Développement et diffusion massive des technologies de l''information et de la communication (ordinateurs personnels, internet, smartphones, intelligence artificielle...), transformant en profondeur la communication, l''accès à l''information, l''économie (e-commerce, économie numérique), la culture et la société.', '1990-01-01', 'Phénomène global.', NULL, NULL, '["Technologie", "Société", "Économie", "Communication", "Culture"]', '["Negroponte, Nicholas. Being Digital."]', NOW(), NOW()),
(98, 5, 'Changement climatique anthropique', 'Modification à long terme des modèles météorologiques et des températures moyennes de la planète, principalement due aux émissions de gaz à effet de serre (CO2, méthane...) issues des activités humaines (combustion d''énergies fossiles, déforestation, agriculture intensive). Représente un défi environnemental, social et économique majeur pour l''avenir.', '1995-01-01', 'Phénomène global.', NULL, NULL, '["Environnement", "Science", "Climat", "Société", "Économie"]', '["IPCC (GIEC). Rapports d\'évaluation (ex: Climate Change 2021: The Physical Science Basis)."]', NOW(), NOW()),
(99, 5, 'Mouvements sociaux contemporains', 'Mobilisations citoyennes, souvent facilitées par les réseaux sociaux numériques, pour des causes variées telles que la démocratie, la justice sociale et économique, les droits humains (minorités, femmes, LGBTQ+), la protection de l''environnement, etc.', '1996-05-01', 'Phénomène global (ex: Printemps arabe, Occupy Wall Street, #MeToo, Black Lives Matter, manifestations pour le climat...).', NULL, NULL, '["Politique", "Société", "Activisme", "Droits de l''homme", "Environnement"]', '["Castells, Manuel. Networks of Outrage and Hope."]', NOW(), NOW());
